(function(e){e(document).ready(function(){var t=JSON.parse(e("#ald-settings").val()),n=t.theme,r=t.name,i=t.path+n+"/";e("input.ald-previewer").bind("keyup",function(t){var n=e(t.target).val(),r=e("#ald-echo");r.html(n);if(n===""){r.fadeOut()}else{r.empty();for(var s in n){if(n.charAt(s).match(/[a-zA-Z]/)){var o=n.charAt(s),u=e("<img>").addClass("ald-image");u.attr("src",i+o.toLowerCase()+".png");u.attr("alt",o);u.attr("title",o);r.append(u)}else{var a=e("<div>").addClass("ald-spacer");r.append(a)}}r.fadeIn()}});if(r!==""){e("input.ald-previewer").val(r).trigger("keyup")}})})(jQuery)